package basicJava;

public class whileDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//while loop
		int i = 0;
		
		while(i<10)
		{
			System.out.println(i);
			i++; //i=2
		}
		
		int j=20;
		do
		{			
			System.out.println(j);
			j++;
		}while(j<30); // 1 loop of execution is guarantee
	}
}
